INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (144, 'Ochronny Posążek', '<p><strong>Dowolna faza:</strong> Odrzuć Ochronny Posążek po nieudanym <strong>teście walki</strong>, aby zredukować obrażenia zadane przez potwora do 0. Możesz skorzystać z tego przedmiotu, aby na 1 turę zapobiec wszstkim obrażeniom zadawanym przez Przedwiecznego.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/144/1735644107060-1735573626310-card155.jpg', 'http://localhost:3000/public/cards/144/1735644108240-card170.jpg', 'pl', '2024-12-23 18:20:49.35812+01', '2024-12-31 12:21:48.24+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (120, 'Księga Dzyan', '<p><strong><em>Księga</em></strong></p><p></p><p><strong>Ruch:</strong> Zakryj tę kartę i wydaj 2 punkty ruchu, aby wykonać <strong>test Wiedzy (-1).</strong> Jeśli test się powiedzie, wylosuj 1 Zaklęcie, odrzuć 1 punkt Poczytalności i umieść 1 żeton Wytrzymałości (z puli) na Księdze Dzyan. Odrzuć Księgę Dzyan, jeśli znajdują się na niej 2 żetony Wytrzymałości. Jeśli test się nie powiedzie, nic sięnie dzieje.</p>', 'unique_item', 'book', '[]', NULL, 'http://localhost:3000/public/cards/120/1735643763895-1735573151858-card131.jpg', 'http://localhost:3000/public/cards/120/1735643765047-card170.jpg', 'pl', '2024-12-23 17:44:35.622684+01', '2024-12-31 12:16:05.048+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (128, 'Dziwna Statuetka', '<p><strong>Ruch:</strong> Zakryj tę kartę, wydaj 2 punkty ruchu i odrzuć 1 punkt Poczytalności, aby rzucić kością. Jeśli uzyskasz sukces, wylosuj 1 Zaklęcie lub weź 3 żetony Wskazówek. Jeśli nie uzyskasz sukcesu, tracisz 2 punkty Wytrzymałości.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/128/1735643859689-1735573277534-card139.jpg', 'http://localhost:3000/public/cards/128/1735643860839-card170.jpg', 'pl', '2024-12-23 17:57:07.546078+01', '2024-12-31 12:17:40.842+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (148, 'Przyłącz się do Zwycięzcy', '<p><strong><em>Misja</em></strong></p><p></p><p><strong>Szpital Św. Marii</strong></p><p><strong>Przytułek w Arkham</strong></p><p><strong>Południowy Kościół</strong></p><p><strong>Zagubiony w Czasie i Przestrzeni</strong></p><p></p><p><strong>Poświęć: </strong>1 Sprzymierzeńca</p><p></p><p><strong>Efekt: </strong>Wygrywasz grę, Pozostali Badacze przegrywają grę.</p>', 'unique_item', 'quest', '[]', NULL, 'http://localhost:3000/public/cards/148/1735644172441-1735573700788-card159.jpg', 'http://localhost:3000/public/cards/148/1735644173598-card170.jpg', 'pl', '2024-12-23 18:26:09.412545+01', '2024-12-31 12:22:53.598+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (154, 'Srebrny Klucz', '<p><strong>Dowolna faza: </strong>Umieść 1 żeton Wytrzymałości (z puli) na Srebrnym Kluczu, aby automatycznie zdać <strong>test uników.</strong> Odrzuć Srebrny Klucz, jeśli znajdują się na nim 3 żetony Wytrzymałości.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/154/1735644229470-1735573847507-card164.jpg', 'http://localhost:3000/public/cards/154/1735644230728-card170.jpg', 'pl', '2024-12-30 14:04:35.555988+01', '2024-12-31 12:23:50.729+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (158, 'Starożytna Tabliczka', '<p><strong>Ruch: </strong>Wydaj 3 punkty ruchu i odrzuć Starożytną Tabliczkę, aby rzucić 2 kośćmi. Za każdy uzyskany sukces wylosuj 1 Zaklęcie. Za każdą kość, na której nie uzyskasz sukcesu, otrzymujesz 2 żetony Wskazówek.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/158/1735644284781-1735573910657-card168.jpg', 'http://localhost:3000/public/cards/158/1735644286019-card170.jpg', 'pl', '2024-12-30 14:08:49.290338+01', '2024-12-31 12:24:46.021+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (168, 'Ruina', '<p><strong>Modyfikator rzucania</strong>: -2</p><p><strong>Koszt Poczytalności: 1</strong></p><p></p><p><strong><em>Magiczne zaklęcie</em></strong></p><p></p><p><strong>Dowolna faza: </strong>Rzuć zaklęcie i zakryj tę kartę przed wykonianiem <strong>testu walki.</strong> Ta walka natychmiast się kończy, tak jakbyś wykonał udany <strong>test uników.</strong> Odrzuć to Zaklęcie, jeśli Przedwieczny się przebudzi.</p>', 'spell', NULL, '[{"modifier":"spell_test","value":-2}]', NULL, 'http://localhost:3000/public/cards/168/1735644396717-1735574079452-card179.jpg', 'http://localhost:3000/public/cards/168/1735644397846-card192.jpg', 'pl', '2024-12-30 14:28:05.810961+01', '2024-12-31 12:26:37.846+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (170, 'Błogosławieństwo', '<p><strong>Modyfikator rzucania</strong>: -1</p><p><strong>Koszt Poczytalności: </strong>2</p><p></p><p><strong><em>Magiczne zaklęcie</em></strong></p><p></p><p><strong>Utrzymanie:</strong> Rzuć zaklęcie i odrzuć tę kartę, aby wybrać jednego Badacza (nawet siebie). Ten Badasz zostanie <em>Pobłogosławiony.</em></p>', 'spell', NULL, '[{"modifier":"spell_test","value":-1}]', NULL, 'http://localhost:3000/public/cards/170/1735644422756-1735574109917-card181.jpg', 'http://localhost:3000/public/cards/170/1735644423877-card192.jpg', 'pl', '2024-12-30 14:30:20.822285+01', '2024-12-31 12:27:03.878+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (176, 'Ochrona Ciała', '<p><strong>Modyfikator rzucania</strong>: -2</p><p><strong>Koszt Poczytalności: 1</strong></p><p></p><p><strong><em>Magiczne zaklęcie</em></strong></p><p></p><p><strong>Dowolna faza: </strong>Rzuć zaklęcie i odrzuć tę kartę, aby zapobiec utracie wszystkich punktów Wytrzymałości z jednego źródła. Odrzuć tę kartę, jeśli Przedwieczny się przebudzi.</p>', 'spell', NULL, '[{"modifier":"spell_test","value":-2}]', NULL, 'http://localhost:3000/public/cards/176/1735644487225-1735574236868-card187.jpg', 'http://localhost:3000/public/cards/176/1735644488370-card192.jpg', 'pl', '2024-12-30 14:40:21.34961+01', '2024-12-31 12:28:08.371+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (61, 'Waleczność', '<p><strong>+1 Waleczność</strong></p><p></p><p>Kiedy wydajesz żeton Wskazówki, aby wspomóc<strong> test Walecznośc</strong>i, otrzymujesz jedną dodatkową kość.</p>', 'ability', NULL, '[]', NULL, 'http://localhost:3000/public/cards/61/1735642750757-1735571980857-card070.jpg', 'http://localhost:3000/public/cards/61/1735642751926-card086.jpg', 'pl', '2024-12-23 16:36:35.612684+01', '2024-12-31 11:59:11.926+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (65, 'Przeszukiwanie', '<p>Kiedy wykonujesz<strong> test Szczęścia</strong> i sprawdzasz uzyskane sukcesy, do wyniku każdej kości w rzucie dodaj +1 (wynik &quot;3&quot; traktuj jako &quot;4&quot; itd.).</p>', 'ability', NULL, '[]', NULL, 'http://localhost:3000/public/cards/65/1735642855785-1735572041961-card074.jpg', 'http://localhost:3000/public/cards/65/1735642856947-card086.jpg', 'pl', '2024-12-23 16:39:06.087307+01', '2024-12-31 12:00:56.948+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (136, 'Zapieczętowanie Mocy Bestii', '<p><strong><em>Misja</em></strong></p><p></p><p><strong>Zajazd Hibba</strong></p><p><strong>Biblioteka</strong></p><p><strong>Stary Magiczny Sklepik</strong></p><p></p><p><strong>Poświęć:</strong> 1 punkt Poczytalności</p><p></p><p><strong>Efekt:</strong> Jeśli Przedwieczny się przebudzi, jego wartość walki zostaje zmniejszona o 2 (np. jeśli wynosiła -5, zostanie zmniejszona do -3).</p>', 'unique_item', 'quest', '[]', NULL, 'http://localhost:3000/public/cards/136/1735643990586-1735573477605-card147.jpg', 'http://localhost:3000/public/cards/136/1735643991770-card170.jpg', 'pl', '2024-12-23 18:08:02.032868+01', '2024-12-31 12:19:51.771+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (4, 'Pożyczka Bankowa', '<p>Kiedy bierzesz tę kartę, otrzymujesz 10$.</p><p></p><p><strong>Utrzymanie:</strong> Rzuć kością. Jeżeli wypadnie 1-3, musisz zapłacić 1$ albo odrzucić wszystkie posiadane przdmioty oraz tę karttę. Podczas tej rozgrywki nie możesz już otrzymać pożyczki bankowej.</p><p></p><p><strong>Dowolna faza:</strong> Zapłać 10$, aby spłacić swoją pożyczkę bankową i odrzucić tę kartę. W dalszej części rozgrywki możesz zaciągnąć nową pożyczkę bankową.</p>', 'honorarium', NULL, '[]', NULL, 'http://localhost:3000/public/cards/4/1735641943270-1735570991607-card003.jpg', 'http://localhost:3000/public/cards/4/1735641944406-card025.jpg', 'pl', '2024-12-05 13:58:17.268004+01', '2024-12-31 11:45:44.407+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (152, 'Płomień Aszurbanipala', '<p>Kiedy jakikolwiek Badacz posiada Płomień Aszurbanipala, maksymalna liczba bram, które mogą być otwarte, zanim przebudzi się Przedwieczny, wzrasta o 1.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/152/1735644204466-1735573775794-card162.jpg', 'http://localhost:3000/public/cards/152/1735644205659-card170.jpg', 'pl', '2024-12-30 14:01:57.395681+01', '2024-12-31 12:23:25.66+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (5, 'Wóz Patrolowy', '<p><strong>Ruch: </strong>Jeśli znajdujesz się w Arkham, możesz, zamiast normalnego ruchu, przesunąć się na dowolne pole ulicy lub obszar w Arkham.</p><p></p><p>Za każdym razem kiedy zakończysz walkę lub wrócisz do Arkham z Innego Świata, rzuć kością, Jeśli wypadnie &quot;1&quot; odrzuć tę kartę do pudełka.</p>', 'location', NULL, '[]', NULL, 'http://localhost:3000/public/cards/5/1735641969840-1735571004649-card004.jpg', 'http://localhost:3000/public/cards/5/1735641971130-card026.jpg', 'pl', '2024-12-05 14:02:47.819078+01', '2024-12-31 11:46:11.13+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (9, 'Członkostwo w Gangu Sheldona', '<p><strong>Utrzymanie: </strong>Jeśli Znajdujesz się na obszarze ulicy, możesz oddać się drobnej przestępczości i rzucić kością. Odczytaj wynik z poniższej listy:</p><p></p><p>1: Zostajesz aresztowany. Trafiasz na Posterunek Policji.</p><p>2-3: Nic się nie dzieje.</p><p>4: Otrzymujesz 2$.</p><p>5: Wylosuj 1 Przedmiot powrzechny.</p><p>6: Wylosuj 1 Przedmiot unikatowy.</p>', 'location', NULL, '[]', NULL, 'http://localhost:3000/public/cards/9/1735642041654-1735571108295-card008.jpg', 'http://localhost:3000/public/cards/9/1735642042793-card027.jpg', 'pl', '2024-12-05 14:12:58.114358+01', '2024-12-31 11:47:22.794+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (19, 'Klaustrofobia', '<p><strong>Utrzymanie: </strong>Jeśli znajdujesz się na jakimś obszarze (wyłączywszy Przytułek w Arkham i Inne Światy), tracisz 1 punkt Poczytalności.</p><p></p><p>Jeśli kiedykolwiek otrzymasz drugą kartę <em>Klaustrofobii</em>, zostaniesz <strong>pochłonięty.</strong></p>', 'madness', NULL, '[]', NULL, 'http://localhost:3000/public/cards/19/1735642174076-1735571258062-card018.jpg', 'http://localhost:3000/public/cards/19/1735642175268-card031.jpg', 'pl', '2024-12-05 14:38:23.176242+01', '2024-12-31 11:49:35.269+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (32, 'Kontuzja Pleców', '<p>Nie możesz posiadać więcej niż 3 Przedmiotów unikatowych (w sumie). Jeśli w dowolnym momencie będziesz posiadał więcej takich kart, musisz odrzucić ich nadmiar.</p><p></p><p>Jeśli kledykolwiek otrzymasz drugą kartę <em>Kontuzji Pleców</em>, zostaniesz<strong> pochłonięty.</strong></p>', 'wound', NULL, '[{"modifier":"max_common_unique_items","value":3}]', NULL, 'http://localhost:3000/public/cards/32/1735642346822-1735571481968-card040.jpg', 'http://localhost:3000/public/cards/32/1735642347955-card034.jpg', 'pl', '2024-12-11 14:47:13.624255+01', '2024-12-31 11:52:27.955+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (25, 'Rana Głowy', '<p><strong>Wiedza -1</strong></p><p></p><p>Dodając kości do <strong>testów Wiedzy,</strong> nie możesz wydawać więcej niż 2 żetony Wskazówek.</p><p></p><p>Jeśli kiedykolwiek otrzymasz drugą kartę <em>Rany Głowy</em>, zostaniesz <strong>pochłonięty.</strong></p>', 'wound', NULL, '[{"modifier":"knowledge","value":-1}]', NULL, 'http://localhost:3000/public/cards/25/1735642258092-1735571359277-card032.jpg', 'http://localhost:3000/public/cards/25/1735642259244-card034.jpg', 'pl', '2024-12-10 19:49:17.999302+01', '2024-12-31 11:50:59.244+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (26, 'Utrata Pamięci', '<p>Nie możesz posiadać więcej niż 3 Umiejętności i Zaklęć (w sumie). Jeśli w dowolnym momencie będziesz posiadał więcej takich kart, musisz odrzucić ich nadmiar.</p><p></p><p>Jeśli kledykolwiek otrzymasz drugą kartę <em>Utraty Pamięci</em>, zostaniesz<strong> pochłonięty.</strong></p>', 'wound', NULL, '[{"modifier":"max_skills_spells","value":3}]', NULL, 'http://localhost:3000/public/cards/26/1735642268240-1735571381558-card033.jpg', 'http://localhost:3000/public/cards/26/1735642269359-card034.jpg', 'pl', '2024-12-11 14:36:34.111144+01', '2024-12-31 11:51:09.36+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (35, 'Kontuzja Ramienia', '<p><strong>Waleczność -1</strong></p><p></p><p>Dodając kości do <strong>testów Waleczności, </strong>nie możesz wydawać więcej niż 2 żetony Wskazówek.</p><p></p><p>Jeśli kledykolwiek otrzymasz drugą kartę <em>Kontuzji Ramienia</em>, zostaniesz<strong> pochłonięty.</strong></p>', 'wound', NULL, '[{"modifier":"prowess","value":-1}]', NULL, 'http://localhost:3000/public/cards/35/1735642392361-1735571519431-card043.jpg', 'http://localhost:3000/public/cards/35/1735642393541-card034.jpg', 'pl', '2024-12-11 14:52:39.964753+01', '2024-12-31 11:53:13.542+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (39, 'Klątwa', '<p><strong>Utrzymanie:</strong> Rzuć kością. Jeśli wypadnie &quot;1&quot;, odrzuć tę kartę.</p><p></p><p><strong>Dowolna faza: </strong>Kiedy rzucasz kośmi, uzyskujesz sukcesy na wynikau &quot;6&quot;. Jeśli zostaniesz <em>Pobłogosławiony</em>, zamiast otrzymać kartę Błogosławieństwa, odrzuć tę kartę.</p>', 'curse', NULL, '[]', NULL, 'http://localhost:3000/public/cards/39/1735642461292-1735571581647-card047.jpg', 'http://localhost:3000/public/cards/39/1735642462451-card047.jpg', 'pl', '2024-12-11 14:57:54.991798+01', '2024-12-31 11:54:22.451+01');
INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (47, 'Zebulon Whateley', '<p><strong>Wola +1, Wiedza +1</strong></p><p></p><p><strong>Dowolna faza: </strong>Zakryj tę kartę, aby odwrócić jedno z twoich zakrytych <em>Zaklęć.</em></p>', 'ally', NULL, '[{"modifier":"will","value":1},{"modifier":"knowledge","value":1}]', NULL, 'http://localhost:3000/public/cards/47/1735642560599-1735571712579-card055.jpg', 'http://localhost:3000/public/cards/47/1735642561777-card064.jpg', 'pl', '2024-12-23 16:19:32.344181+01', '2024-12-31 11:56:01.777+01');
