INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (159, 'Oczyszczenie Miasteczka', '<p><strong><em>Misja</em></strong></p><p></p><p><strong>Posterunek Policji</strong></p><p><strong>Redakcja</strong></p><p><strong>Stary Magiczny Sklepik</strong></p><p><strong>Południowy Kościół</strong></p><p></p><p><strong>Poświęć</strong>: 1 Zaklęcie</p><p></p><p><strong>Efekt</strong>: Wszystkie karty <strong><em>Pogłosek</em></strong> w grze z talii Mitów należy odłożyć do pudełka.</p>', 'unique_item', 'quest', '[]', NULL, 'http://localhost:3000/public/cards/159/1735644296715-1735573924376-card169.jpg', 'http://localhost:3000/public/cards/159/1735644297856-card170.jpg', 'pl', '2024-12-30 14:10:31.004855+01', '2024-12-31 12:24:57.856+01');
