INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (113, 'Znak Starszych Bogów', '<p><strong>Faza spotkań w Arkham:</strong> Kiedy zamykasz bramę, odrzuć po 1 punkcie Wytrzymałości i Poczytalności i odłóż tę kartę do pudełka. Nie muszisz wykonywać testu umiejętności lub wydawać żetonów Wskazówek, aby zapieczętować bramę. Dodatkowo, usuń jeden żeton zagłady z toru zagłady Przedwiecznego.</p>', 'unique_item', NULL, '[]', NULL, 'http://localhost:3000/public/cards/113/1735643659409-1735573044660-card124.jpg', 'http://localhost:3000/public/cards/113/1735643660548-card170.jpg', 'pl', '2024-12-23 17:32:30.269129+01', '2024-12-31 12:14:20.548+01');
