INSERT INTO public.card (id, name, description, type, subtype, attribute_modifiers, hand_usage, front_image_path, back_image_path, locale, created_at, updated_at) VALUES (160, 'Spętanie Potwora', '<p><strong>Modyfikator rzucania</strong>: +4</p><p><strong>Koszt Poczytalności: </strong>2</p><p></p><p><strong><em>Magiczne zaklęcie</em></strong></p><p></p><p><strong>Dowolna faza:</strong> Rzuć zaklęcie i odrzuć tę kartę, aby automatycznie zdać jeden<strong> test walki</strong>. Aby rzucić to zaklęcie, muszisz uzyskać tyle sukcesów, ile wynosi żywotność potwora. To zaklęcie nie działa na Przedwiecznego.</p>', 'spell', NULL, '[{"modifier":"spell_test","value":4}]', 2, 'http://localhost:3000/public/cards/160/1735644309280-1735573939789-card171.jpg', 'http://localhost:3000/public/cards/160/1735644310688-card192.jpg', 'pl', '2024-12-30 14:13:59.406079+01', '2024-12-31 12:25:10.689+01');
